import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
    const navigate = useNavigate();

    const handleLogin = () => {
        navigate('/login');
    };

    const handleRegister = () => {
        navigate('/register');
    };

    return (
        <div className="homeContainer">
            <nav className="navbar">
                <div className="appName">Resume Review Platform</div>
                <div>
                    <button className="navButton" onClick={handleLogin}>Login</button>
                    <button className="navButton" onClick={handleRegister}>Sign Up</button>
                </div>
            </nav>
            <header className="header">
                <h1>Welcome to the Resume Review Platform</h1>
                <p>
                    This platform allows students to upload their resumes and get feedback from experts. Experts can register and review the resumes, providing valuable insights and suggestions.
                </p>
            </header>
            <section className="tipsSection">
                <h2>Resume Tips</h2>
                <ul>
                    <li>Keep it concise and to the point.</li>
                    <li>Use action verbs to describe your experiences.</li>
                    <li>Tailor your resume for each job application.</li>
                    <li>Include relevant keywords from the job description.</li>
                    <li>Proofread for grammar and spelling errors.</li>
                </ul>
            </section>
            <footer className="footer">
                &copy; 2024 Resume Review Platform. All rights reserved.
            </footer>
        </div>
    );
};

export default Home;
