import React, { useEffect, useState } from 'react';
import './myResumes.css'
const Api_URL = "http://localhost:5000";

const MyResumes = () => {
    const [resumes, setResumes] = useState([]);

    useEffect(() => {
        const fetchResumes = async () => {
            try {
                const token = localStorage.getItem('auth-token');
                const response = await fetch(Api_URL + '/myresumes', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });
                const responseData = await response.json();
                if (response.ok) {
                    setResumes(responseData);
                } else {
                    alert(responseData.message || 'Error fetching resumes');
                }
            } catch (error) {
                console.error(error);
                alert('Error fetching resumes');
            }
        };

        fetchResumes();
    }, []);

    return (
        <div>
             <h1>My Resumes</h1>
            {resumes.length > 0 ? (
                <div className="resume-cards">
                    {resumes.map((resume) => (
                        <div key={resume._id} className="resume-card">
                            <iframe 
                                src={`http://localhost:5000/${resume.resumeFile}`} 
                                title="Resume" 
                                className="resume-iframe"
                            />
                            <h3>Reviews:</h3>
                            {resume.reviews.length > 0 ? (
                                <ul>
                                    {resume.reviews.map((review) => (
                                        <li key={review._id}>
                                            <p><strong>Expert:</strong> {review.expert.name}</p>
                                            <p>{review.review}</p>
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No reviews yet.</p>
                            )}
                        </div>
                    ))}
                </div>
            ) : (
                <p>No resumes uploaded yet.</p>
            )}
        </div>
    );
};

export default MyResumes;
