import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
// import './Home.css';

const Home = () => {
    const navigate = useNavigate();
    const [userRole, setUserRole] = useState(null);

    useEffect(() => {
        // Example check for user role
        const role = localStorage.getItem('userRole'); // Assume 'userRole' is stored in localStorage
        setUserRole(role);
    }, []);

    const handleLogin = () => {
        navigate('/login');
    };

    const handleRegister = () => {
        navigate('/register');
    };

    const handleMyResumes = () => {
        navigate('/myresumes');
    };

    const handleUploadResume = () => {
        navigate('/upload');
    };

    const handleExpertDashboard = () => {
        navigate('/expertdashboard');
    };

    return (
        <div>
            <nav className="navbar">
                <div className="nav-brand">Resume Review Platform</div>
                <div className="nav-links">
                    <button onClick={handleLogin}>Login</button>
                    <button onClick={handleRegister}>Sign Up</button>
                </div>
            </nav>
            <div className="home-container">
                <h1>Welcome to the Resume Review Platform</h1>
                <p>
                    This platform allows students to upload their resumes and get feedback from experts. Experts can register and review the resumes, providing valuable insights and suggestions.
                </p>
                <div className="resume-tips">
                    <h2>Resume Tips</h2>
                    <ul>
                        <li>Keep it concise and to the point.</li>
                        <li>Highlight your achievements.</li>
                        <li>Use bullet points for readability.</li>
                        <li>Ensure there are no grammatical errors.</li>
                    </ul>
                </div>
                <div className="user-options">
                    {userRole === 'student' && (
                        <div className="student-options">
                            <button onClick={handleMyResumes}>My Resumes</button>
                            <button onClick={handleUploadResume}>Upload Resume</button>
                        </div>
                    )}
                    {userRole === 'expert' && (
                        <div className="expert-options">
                            <button onClick={handleExpertDashboard}>Expert Dashboard</button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Home;
